mutation ServiceAccountCreate(
    $name: String, $is_active: Boolean $permissions: [PermissionEnum]){
    serviceAccountCreate(input:
        {name: $name, isActive: $is_active, permissions: $permissions})
    {
        authToken
        serviceAccount{
            permissions{
                code
                name
            }
            id
            isActive
            name
            tokens{
                authToken
            }
        }
        accountErrors{
            field
            message
            code
        }
    }
}
