query:
mutation attributeTranslate($attributeId: ID!) {
    attributeTranslate(
            id: $attributeId, languageCode: PL,
            input: {name: "Kolor PL"}) {
        attribute {
            translation(languageCode: PL) {
                name
                language {
                    code
                }
            }
        }
    }
}
