mutation InvoiceCreate($orderId: ID!, $number: String!, $url: String!) {
    invoiceCreate(
        orderId: $orderId,
        input: {
            number: $number,
            url: $url
        }
    ) {
        invoice {
            status
            number
            url
        }
        invoiceErrors {
            field
            code
        }
    }
}
