mutation ProductVariantUpdate(
    $id: ID!,
    $price: PositiveDecimal,
    $costPrice: PositiveDecimal,
) {
    productVariantUpdate(
        id: $id,
        input: {
            costPrice: $costPrice,
            price: $price,
        }
    ) {
        errors {
            field
            message
        }
        productVariant {
            costPrice{
                amount
            }
        }
    }
}
