query OrdersQuery {
    orders(first: 1) {
        edges {
            node {
                lines {
                    thumbnail(size: 540) {
                        url
                    }
                    variant {
                        id
                    }
                    quantity
                    allocations {
                        id
                        quantity
                        warehouse {
                            id
                        }
                    }
                    unitPrice {
                        currency
                        gross {
                            amount
                        }
                    }
                    totalPrice {
                        currency
                        gross {
                            amount
                        }
                    }
                }
            }
        }
    }
}
