mutation CreateUserAddress($user: ID!, $city: String!, $country: CountryCode!) {
    addressCreate(userId: $user, input: {city: $city, country: $country}) {
        errors {
            field
            message
        }
        address {
            id
            city
            country {
                code
            }
        }
        user {
            id
        }
    }
}
