query:
mutation mc($name: String!, $collection: ID,
        $category: ID, $page: ID, $url: String) {

    menuCreate(input: {
        name: $name,
        items: [
            {name: "Collection item", collection: $collection},
            {name: "Page item", page: $page},
            {name: "Category item", category: $category},
            {name: "Url item", url: $url}]
    }) {
        menu {
            name
            slug
            items {
                id
            }
        }
    }
}
