mutation createProduct(
    $productTypeId: ID!,
    $categoryId: ID!
    $name: String!,
    $basePrice: PositiveDecimal!,
    $sku: String,
    $trackInventory: Boolean)
{
    productCreate(
        input: {
            category: $categoryId,
            productType: $productTypeId,
            name: $name,
            basePrice: $basePrice,
            sku: $sku,
            trackInventory: $trackInventory
        })
    {
        product {
            id
            name
            slug
            variants{
                id
                sku
                trackInventory
                quantity
                price {
                    amount
                }
            }
            category {
                name
            }
            productType {
                name
            }
        }
        errors {
            message
            field
        }
    }
}
