query {
    products(sortBy: {field: PUBLISHED, direction:ASC}, first: 2) {
        edges {
            node {
                isPublished
                productType{
                    name
                }
                pricing {
                    priceRangeUndiscounted {
                        start {
                            gross {
                                amount
                            }
                        }
                    }
                    priceRange {
                        start {
                            gross {
                                amount
                            }
                        }
                    }
                }
                updatedAt
            }
        }
    }
}
