query (
    $first: Int, $last: Int, $after: String, $before: String,
    $sortBy: OrderSortingInput, $filter: OrderFilterInput
){
    orders(
        first: $first, last: $last, after: $after, before: $before,
        sortBy: $sortBy, filter: $filter
    ) {
        totalCount
        edges {
            node {
                id
                number
                total{
                    gross{
                        amount
                    }
                }
            }
        }
        pageInfo{
            startCursor
            endCursor
            hasNextPage
            hasPreviousPage
        }
    }
}
