query:
mutation AppInstall(
    $app_name: String, $manifest_url: String, $permissions: [PermissionEnum]){
    appInstall(
        input:{appName: $app_name, manifestUrl: $manifest_url,
            permissions:$permissions}){
        appInstallation{
            id
            status
            appName
            manifestUrl
        }
        appErrors{
            field
            message
            code
            permissions
        }
    }
}
