mutation UpdatePublicMetadata($id: ID!, $input: [MetadataInput!]!) {
    updateMetadata(
        id: $id
        input: $input
    ) {
        metadataErrors{
            field
            code
            message
        }
        item {
            metadata{
                key
                value
            }
            ...on ProductType{
                id
            }
        }
    }
}
