mutation invoiceUpdate($id: ID!, $number: String, $url: String) {
    invoiceUpdate(
        id: $id
        input: {
            number: $number
            url: $url
        }
    ) {
        invoice {
            id
            number
            url
            metadata {
                key
                value
            }
        }
        invoiceErrors {
            field
            code
        }
    }
}
