query:
mutation draftUpdate(
    $id: ID!, $voucher: ID!, $customerNote: String, $shippingAddress: AddressInput
) {
    draftOrderUpdate(id: $id,
                        input: {
                            voucher: $voucher,
                            customerNote: $customerNote,
                            shippingAddress: $shippingAddress,
                        }) {
        orderErrors {
            field
            message
            code
        }
        order {
            userEmail
        }
    }
}
