query:
mutation RegisterAccount(
    $password: String!,
    $email: String!,
    $redirectUrl: String
) {
    accountRegister(
        input: {
            password: $password,
            email: $email,
            redirectUrl: $redirectUrl
        }
    ) {
        accountErrors {
            field
            message
            code
        }
        user {
            id
        }
    }
}
