mutation productVariantTranslate($productVariantId: ID!) {
    productVariantTranslate(
            id: $productVariantId, languageCode: PL,
            input: {name: "Wariant PL"}) {
        productVariant {
            translation(languageCode: PL) {
                name
                language {
                    code
                }
            }
        }
    }
}
