query:
mutation PositiveDecimalInput(
    $id: ID!,
    $cost: PositiveDecimal,
    $price: PositiveDecimal,
) {
    productVariantUpdate(
        id: $id,
        input: {
            costPrice: $cost,
            price: $price,
        }
    ) {
        errors {
            field
            message
        }
        productVariant {
            costPrice {
                amount
            }
        }
    }
}
