query getValidator(
    $country_code: CountryCode!, $country_area: String, $city_area: String) {
    addressValidationRules(
            countryCode: $country_code,
            countryArea: $country_area,
            cityArea: $city_area) {
        countryCode
        countryName
        countryAreaType
        countryAreaChoices {
            verbose
            raw
        }
        cityType
        cityChoices {
            raw
            verbose
        }
        cityAreaType
        cityAreaChoices {
            raw
            verbose
        }
    }
}
