query:
fragment User on User {
  id
  email
  firstName
  lastName
  userPermissions {
    code
    name
    __typename
  }
  avatar {
    url
    __typename
  }
  __typename
}

mutation VerifyToken($token: String!) {
  tokenVerify(token: $token) {
    payload
    user {
      ...User
      __typename
    }
    __typename
  }
}
