query:
mutation pluginUpdate(
    $id: ID!
    $active: Boolean
    $configuration: [ConfigurationItemInput]
) {
    pluginUpdate(
        id: $id
        input: { active: $active, configuration: $configuration }
    ) {
        plugin {
            name
            description
            active
            configuration {
                name
                value
                type
                helpText
                label
            }
        }
        errors {
            field
            message
        }
        pluginsErrors {
            field
            code
        }
    }
}
